const __getElement__ = (query) => {
  return document.querySelector(query);
};
const __changeValue__ = (doc, value) => {
  doc.innerHTML = value;
};
const __getValue__ = (doc) => {
  return doc.innerHTML;
};
const setState = (query, value) => {
  let temp = __getElement__(query);
  for (var i = 0; i < temp.length; i++){
    temp[i].__changeValue__(value);
  }
}
